//package ae.rta.util.vo;package com.dataObject;public class ChoiceWrapper extends Choice {        private Integer integer;         public ChoiceWrapper(Choice choice) {        super(choice);        if (choice != null && choice.getBoolean()) {            setInteger(TRUE);         } else {            setInteger(FALSE);            }            }    public ChoiceWrapper(Boolean boolean1) {        super(boolean1);          if (boolean1 == true) {            setInteger(TRUE);           } else {            setInteger(FALSE);           }    }    public ChoiceWrapper(boolean b) {        super(b);        if (b == true) {            setInteger(TRUE);           } else {            setInteger(FALSE);           }    }    public ChoiceWrapper(Long long1) {        super(long1);        if (long1 != null) {            setInteger(long1.intValue());        }    }    public ChoiceWrapper(int i) {                super(i);        setInteger(i);    }    public ChoiceWrapper(Integer integer) {        super(integer);        setInteger(integer);    }    public ChoiceWrapper(String string) {        this(new Integer(string));            }    public ChoiceWrapper() {          super();        setInteger(ChoiceWrapper.FALSE);    }    public void setInteger(Integer integer) {        setDomainValue(integer);        this.integer = integer;    }     /**     * Get Integer value for this choice.     *      * @return Integer value for this choice.     */    public Integer getInteger() {        return this.integer;      }        /**     * Get object String representation. This method is used by toString()     * method to get object string representation.     *      * @return Object String representation.     */    public String toString() {        return getInteger() != null ? getInteger().toString() : null;    }}